import { GET_PLACES, SET_LOADING } from "../actions/types"

const initialState = {
    places:null,
    loading:true,
    currentPlace:null
}


export default (state = initialState, action) => {
    switch(action.type){

        case GET_PLACES:
            return{
                ...state,
                places: action.payload.data,
                loading:false
            }

        case SET_LOADING:
            return{
                ...state,
                loading:true
            }
        default:
            return state
    }
}
