import { useEffect } from "react";
import GoogleMapReact from "google-map-react";
import { connect } from "react-redux";
import { setCoordinates, setBounds, setChildClicked } from "../../redux/actions/TheMap";
import fakeResImg from "../../assets/fakeres.jpg";

import "./map.css"
const Map = ({
  themap: { coordinates, bounds },
  place: { places },
  setCoordinates,
  setBounds,
  setChildClicked
}) => {
  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      ({ coords: { latitude, longitude } }) => {
        setCoordinates(latitude, longitude);
      }
    );
  }, []);

  return (
    <div style={{ height: "100vh", width: "100%" }}>
      <GoogleMapReact
        bootstrapURLKeys={{ key: "AIzaSyC2Svm5V3x3n8C03ySFkHrV0gAt6li2iUs" }}
        defaultCenter={coordinates}
        center={coordinates}
        defaultZoom={14}
        margin={[50, 50, 50, 50]}
        options={""}
        onChange={(e) => {
          setCoordinates(e.center.lat, e.center.lng);
          setBounds(e.marginBounds.ne, e.marginBounds.sw);
        }}
        onChildClick={(child) => setChildClicked(child)}
      >
        {places?.map((pl, i) => (
          <div className="markerContainer"
            key={i}
            lat={Number(pl.latitude)}
            lng={Number(pl.longitude)}
          >
              <div className="map-paper">
                  <span className="paper-title">{pl.name}</span>
                  <img 
                    src={`${ pl.photo ? pl.photo.images.large.url : fakeResImg }`}
                    alt={pl.name} 
                    className="paper-img" />
              </div>
          </div>
        ))}
      </GoogleMapReact>
    </div>
  );
};

const mapStateToProps = (state) => ({
  themap: state.themap,
  place: state.place,
});
export default connect(mapStateToProps, { setCoordinates, setBounds, setChildClicked })(Map);
