import { useState } from "react";
import { Autocomplete } from "@react-google-maps/api";
import { connect } from "react-redux";

import { setCoordinates } from "../../redux/actions/TheMap"


const Header = ({setCoordinates}) => {
  const [autoComplete, setAutoComplete] = useState(null)

 const onLoad = (autoC) => setAutoComplete(autoC)

 const onPlaceChanged = () =>{
   const lat = autoComplete.getPlace().geometry.location.lat();
   const lng = autoComplete.getPlace().geometry.location.lng();

   setCoordinates(lat, lng)
 }
  return (
    <nav className="mb-8">
      <div className="nav-wrapper row blue">
        <div className="col s12 m6">Trip Advisor</div>

        <div className="col s12 m6">
          <Autocomplete onLoad={onLoad} onPlaceChanged={onPlaceChanged}>
            <div className="input-field" style={{ }}>
              <input id="search" type="search" style={{width:"120px", display:"initial"}} required />
              <label className="label-icon" htmlFor="search">
                <i className="material-icons">search</i>
              </label>
              <i className="material-icons">close</i>
            </div>
          </Autocomplete>
        </div>

      </div>
    </nav>
  );
};




export default connect(null, {setCoordinates})(Header);
