import { SET_BOUNDS, SET_COORDINATES } from "../actions/types"

const initialState = {
    map: null,
    coordinates:{lat:0, lng:0},
    bounds:{ne:0, sw:0}

}

// eslint-disable-next-line
export default (state = initialState, action) => {
switch (action.type) {

    case SET_COORDINATES:
        console.log(action.payload)
        return{
            ...state,
            coordinates: state.coordinates.lat = 5.636096,
            coordinates: state.coordinates.lng = -0.196608,
        }

    /* case SET_BOUNDS:
        return{
            ...state,
            bounds: state.bounds.ne = action.payload.ne,
        } */


    default:
        return state
}

}
