import { SET_COORDINATES } from "../actions/types"

const initialState = {

    map: null,
    coordinates:{lat:0, lng:0},
    bounds:null

}

// eslint-disable-next-line
export default (state = initialState, action) => {
switch (action.type) {

    case SET_COORDINATES:
        return{
            ...state,
            coordinates: state.coordinates.lat = action.payload.lat,
            coordinates: state.coordinates.lng = action.payload.lng,
        }


    default:
        return state
}

}
