import { connect } from "react-redux";
import fakeResImg from "../../assets/fakeres.jpg";
import "./placeDetails.css";

const PlaceDetails = ({ place, themap:{ childClicked}, selected, ele, refProp }) => {

if (selected) refProp?.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });

  return (
    <>
      {place && (
        <div className="row place-card" style={{ width: "100%" }}>
          <div className="col s12">
            <div className="card">
              <div className="card-image">
                <img
                  className="place-detail-card-img"
                  src={`${
                    place.photo ? place.photo.images.large.url : fakeResImg
                  }`}
                  title={place.name}
                  alt="place"
                />
                <span className="card-title place-card-title">
                  {place?.name}
                </span>
              </div>
              <div className="card-content">
                {
                  place.ranking ? (
                    <div className="wrap-card-items flex justify-between">
                    <div className="text-sm px-2"><i className="material-icons">insert_chart</i></div>
                    <div className="text-xs">{place.ranking}</div>
                  </div>
                  )
                  :
                  ("")
                }
                <div className="wrap-card-items flex justify-between">
                  <div className="text-sm px-2">
                    <i className="material-icons location">location_on</i>
                  </div>
                  <div className="text-xs">{place.address}</div>
                </div>
                <div className="wrap-card-items flex justify-between">
                  <div className="text-sm px-2">
                    <i className="material-icons">phone</i>
                  </div>
                  <div className="text-xs">{place.phone}</div>
                </div>
                <div className="wrap-card-items flex justify-between">
                  <div className="text-sm px-2">
                    <i className="material-icons">attach_money</i>
                  </div>
                  <div className="text-xs">{place.price}</div>
                </div>
                {place?.cuisine?.map((cuis, i) => (
                  <ul className="place-foods" key={i}>
                    <li className="food-chip text-xs">{cuis.name}</li>
                  </ul>
                ))}
              </div>
              <div className="card-action flex justify-between">
                <a href={`${place.web_url}`}>More</a>
                {place.website && <a href={`${place.website}`}>Website</a>}
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};


const mapStateToProps = state =>({
  themap: state.themap
})
export default connect(mapStateToProps, {})(PlaceDetails);
