import { SET_BOUNDS, SET_COORDINATES } from "../actions/types"

const initialState = {
    map: null,
    coordinates:{
        lat: 0,
        lng: 0
    },
    bounds:{}

}

// eslint-disable-next-line
export default (state = initialState, action) => {
switch (action.type) {
    case SET_COORDINATES:
        return{
            ...state,
            coordinates: {
                lat: action.payload.lat,
                lng: action.payload.lng
            },
        }

    case SET_BOUNDS:
        return{
            ...state,
            bounds: action.payload,
        }


    default:
        return state
}

}
