import { SET_BOUNDS, SET_COORDINATES } from "../actions/types"

const initialState = {
    map: null,
    coordinates:{
        lat:0, 
        lng:0
    },
    bounds:{
        ne:0, 
        sw:0
    }

}

// eslint-disable-next-line
export default (state = initialState, action) => {
switch (action.type) {
    case SET_COORDINATES:
        return{
            ...state,
            coordinates: action.payload,
        }

    /* case SET_BOUNDS:
        return{
            ...state,
            bounds: state.bounds.ne = action.payload.ne,
        } */


    default:
        return state
}

}
