import { useEffect } from "react"
import GoogleMapReact from "google-map-react"
import { connect } from "react-redux"
import { setCoordinates } from "../../redux/actions/TheMap"



const Map = ({themap:{coordinates}}) => {
    useEffect(() => {
       navigator.geolocation.getCurrentPosition(({coords :{latitude, longitude}}) =>{
           setCoordinates(latitude,longitude)
       })
    }, [])
    console.log(coordinates)
    console.log(process.env.REACT_APP_GOOGLE_MAP_API_KEY)
    return (
        <div style={{ height: '100vh', width: '100%' }}>
            <GoogleMapReact
            bootstrapURLKeys={{ key: 'AIzaSyC2Svm5V3x3n8C03ySFkHrV0gAt6li2iUs'}}
            defaultCenter={coordinates}
            center={coordinates}
            defaultZoom={14}
            margin={[50, 50, 50, 50]}
            options={''}
            onChange={(e) => {
                setCoordinates(e.center.lat, e.center.lng)
                setBounds(e.marginBounds.ne, e.marginBounds.sw)
                }
            }
            onChildClick={''}
            >

            </GoogleMapReact>
        </div>
    )
}


const mapStateToProps = state =>({
    themap: state.themap
})
export default connect(mapStateToProps, {})(Map);
